import React, { useContext } from 'react'
import { MyContext } from '../contexts/ThemeContext';
import { WeContext} from '../contexts/AuthContext';


function Navbar() {
    const {isLightTheme,light,dark} =useContext(MyContext);
    const {isAuthenticated,toggleAuth} = useContext(WeContext)
     const theme=isLightTheme?light:dark;
  return (
    <>
       <nav style={{background:theme.ui,color:theme.syntax}}>
        <h1>Context App</h1>
        <div onClick={toggleAuth}>
              {isAuthenticated?'Looged in':"Logged out"}
        </div>
        <ul>
          <li>Home</li>
          <li>About</li>
          <li>Contact</li>
        </ul>
      </nav>
    </>
  )
}

export default Navbar
