import React, { useContext } from 'react'
import { OurContext } from '../contexts/BookContext';
import { MyContext } from '../contexts/ThemeContext';
function BookList() {
  const {isLightTheme,light,dark} = useContext(MyContext)
  const theme=isLightTheme?light:dark;
  const {books} = useContext(OurContext)
  return (
    <>
            <div className="book-list" style={{background:theme.bg,color:theme.syntax}}>
                      <ul>
                        {
                          books.map((value)=>{
                            return (
                            <li style={{background:theme.ui}}>{value.title}</li>
                            )
                          })
                        }
                       
                       
                      </ul>
           </div> 
    </>
  )
}

export default BookList
