import React, { Component } from 'react'
export const MyContext = React.createContext()

 class SampleContext extends Component {
    state={
        
        light:{
            foreground: "#000000",
            background: "#e74c3c"
        },
        dark:{
            foreground: "#ffffff",
            background: "#222222"
        }
    }

    render() {
        return (
            <>
                    <MyContext.Provider value={{...state}}>
                            {this.props.children}
                    </MyContext.Provider> 
            </>
        )
    }
}

export default SampleContext





  
