import React, { useReducer } from 'react'



const initialsState={
     count:0
}
const reducer=(state,action)=>{
   switch(action.type){
     case 'increment':
       return {
           count:state.count+1
       }
       default:return state
   }
}
function App() {
  const [number,dispatch] =useReducer(reducer,initialsState)

  const handleClick=(e)=>{
    dispatch({type:"increment"})
  }
  return (
    <>
           count:{number.count}
           <button onClick={handleClick}>+</button>      
    </>
  )
}

export default App
