import React, { Component } from 'react'
export const WeContext= React.createContext()
export class AuthContext extends Component {
    state={
        isAuthenticated:false
    }

     handleClick=()=>{
         this.setState({
             isAuthenticated:!this.state.isAuthenticated
         })
     }
    render() {
        return (
            <>
                 <WeContext.Provider  value={{...this.state,toggleAuth:this.handleClick}}>
                         {this.props.children}
                 </WeContext.Provider> 
            </>
        )
    }
}

export default AuthContext
