import React, { useEffect, useState } from 'react'
import Recipe from './Recipe';

export default function App() {


     const APP_ID='3238c549';
     const APP_KEY='406d026e8c7a2a1dba579befeded2c08';

   
     const [recipes,setRecipes] =useState([])
     const [search,setSearch] =useState('')
     const [query,setQuery] =useState('')
     


      useEffect(()=>{
                getRecipes()
      },[query])

      



      const getRecipes = async()=>{
           const response = await fetch(`https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`)
           
           const data=await response.json();
           console.log(data.hits)
           setRecipes(data.hits)
       
      }



    const handleChange=(e)=>{
        setSearch(e.target.value)
    }
      
     const handleSubmit=()=>{
           setQuery(search)
     }



    return (
        <>
                <div className='App'>
                                   
                          <form className='search-form' onSubmit={handleSubmit}>
                                 <input type='text' placeholder='' className='search-bar' value={search} onChange={handleChange} />
                                 <button className='search-button'>
                                       click
                                 </button>
                          </form>
                          {
                                recipes.map((value,index)=>{
                                    return(
                                        <Recipe  key ={index} title={value.recipe.label} imgsrc={value.recipe.image} calories={value.recipe.calories}/>
                                    )
                                })
                          }
                </div> 
        </>
    )
}
