import React, { useContext } from 'react'
import {MyContext} from '../contexts/BookContext'



function BookDetails(props) {
    const {removeBooks} = useContext(MyContext)
    
    return (
        <>
               <li className='title' onClick={()=>removeBooks(props.book.id)}>{props.book.title}</li>  
               {/* <li className='author' onClick={()=>removeBooks(props.book.id)}>{props.book.author}</li>   */}
        </>
    )
}

export default BookDetails
