import React,{Component} from 'react';
import User from './User';
export const MyContext=React.createContext()
class App extends Component{

    state={
        name:'animesh'
    }

    render()
    {
        return (
            <div> 
                      <MyContext.Provider value={this.state.name}>
                                    <h1>hello everyone</h1>
                                    <User/>
                      </MyContext.Provider>   
                     
            </div>
        )
    }
}

export default App;
