import React, { useContext } from 'react'
import { MyContext } from '../contexts/ThemeContext';
function Toggle() {
    const {toggleTheme} = useContext(MyContext)
    return (
        <>
                  <button onClick={toggleTheme}>Toggle</button>
        </>
    )
}

export default Toggle
