import React, { useReducer } from 'react'



const initialsState={
     count:0
}
const reducer=(state,action)=>{
   switch(action.type){
     case 'increment':
       return {
           count:state.count+1
       }

        case 'decrement':
       return {
           count:state.count-1
       }

       case 'reset':
        return {
            count:initialsState
        }
       default:return state
   }
}
function App() {
  const [number,dispatch] =useReducer(reducer,initialsState)

  const handleClick=(e)=>{
    dispatch({type:"increment"})
  }

  const handleClicks=(e)=>{
    dispatch({type:"decrement"})
  }

  const handleClicked=()=>{
      dispatch({type:"reset"})
  }
  return (
    <>
           count:{number.count}
           <button onClick={handleClick}>+</button> 
            <button onClick={handleClicks}>-</button> 
            <button onClick={handleClicked}>reset</button>     
    </>
  )
}

export default App
