import React, { Component } from 'react'
import User from './User'
export const MyContext=React.createContext()
export default class App extends Component {
    state={
        name:'animesh'
    }
    render() {
        return (
            <>
                <h1>hello app</h1> 
                 <MyContext.Provider value={this.state.name}>
                        <User/>
                 </MyContext.Provider>
                   
            </>
        )
    }
}
