import React, { Component } from 'react'
import {MyContext} from './App'
export default class Guest extends Component {
    render() {
        return (
            <>
                     <h3>Guest Component</h3>
                   <MyContext.Consumer>
                          {
                              data=><h4>: {data.name} value:{data.value} </h4>
                          }       
                   </MyContext.Consumer>
                   
                  
                   
            </>
        )
    }
}
