import React, { useContext } from 'react'
import {MyContext} from '../contexts/BookContext'



function BookDetails(props) {
    const {removeBooks} = useContext(MyContext)
    return (
        <>
               <li className='title' onClick={removeBooks}>{props.book.title}</li>  
               <li className='author'>{props.book.author}</li>  
        </>
    )
}

export default BookDetails
