import React from 'react'
export const MyContext = React.createContext()
function SampleContext(props) {
    const themes={
        light:{
            foreground: "#000000",
            background: "#eeeeee"
        },
        dark:{
            foreground: "#ffffff",
            background: "#222222"
        }
    }
    return (
        <>
                 <MyContext.Provider value={themes.dark}>
                            {props.children}
                 </MyContext.Provider> 
        </>
    )
}

export default SampleContext







  
