import React,{useState} from 'react'

export default function App() {

    const[name,setName]=useState('animesh')


    const handle=(e)=>{
        setName(e.target.value)
    }



    return (
        <>
                   <input type='text' placeholder='todo here...' value={name} onChange={handle}/>
                   <button>+</button>  

                   <ul>
                           <li>{name}</li>    
                   </ul>  
        </>
    )
}
