import React, { useEffect, useState } from 'react'
import Recipe from './Recipe';

export default function App() {


     const APP_ID='3238c549';
     const APP_KEY='406d026e8c7a2a1dba579befeded2c08';

   
     const [recipes,setRecipes] =useState([])
      


      useEffect(()=>{
                getRecipes()
      },[])

      



      const getRecipes = async()=>{
           const response = await fetch(`https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${APP_KEY}`)
           
           const data=await response.json();
           console.log(data.hits)
           setRecipes(data.hits)
       
      }



    
      
     



    return (
        <>
                <div className='App'>
                                   
                          <form className='search-form'>
                                 <input type='text' placeholder='' className='search-bar'/>
                                 <button className='search-button'>
                                       click
                                 </button>
                          </form>
                          {
                                recipes.map((value,index)=>{
                                    return(
                                        <Recipe  key ={index} title={value.recipe.label} imgsrc={value.recipe.image} calories={value.recipe.calories}/>
                                    )
                                })
                          }
                </div> 
        </>
    )
}
