import React from 'react'
import { useState } from 'react'

function App() {
  const [count,setCount] = useState(0)
  const handleClick=()=>{
       setCount(count+1)
  }
  return (
    <>
           <h1>the value is {count}</h1>   
           <button onClick={handleClick}>Click</button>
    </>
  )
}

export default App
