import React, { Component } from 'react'
import User from './User';

 export const MyContext=React.createContext()
export default class App extends Component {
    state={
        name:'anamika',
        value:10
    }
    handleClickContext=()=>{
        this.setState({value:this.state.value + 1})
    }

    //1.ab hume ye data state={
        // name:'anamika',
        // value:10
        //}

        // or ye data   handleClickContext=()=>{
            // this.setState({value:this.state.value + 1})
        // }

        //2.ye do no hi humare data banenge jo hum provide karenge consumer ko  yahi ki provider ke though isko provide karenge

        //uske liye maine kya hai 1 object bana liya hai contextValue jiske andar maine ye sab chij rakh di hai

        //phir issi ko mai yaha se provide kar raha hon matlab provider provide kar raha hai


        // jis kisi bhi component ko ye wala data chahiye vo consumer ban kar ye data le sakta hai
    

        //3 to kya karte hai render method ke andar 1 const bana lete hai  value={contextValue}



    render() {
        const contextValue={
            data:this.state,
            handleClick:this.handleClickContext
        }
        return (
            <div>
                <MyContext.Provider value={contextValue}>
                     <User/>
                </MyContext.Provider>
            </div>
        )
    }
}


//4 yani ki consumer ke pass ye data jaayega contextValue
