import React, { useEffect, useRef } from 'react'

function Focus() {

    let ref=useRef()

    useEffect(()=>{
          //focus on the input box
       ref.current.focus()
         
    },[])
    
    return (
        <>
                <input type='text' ref={ref}/>
        </>
    )
}

export default Focus
